import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import FileBrowser from './components/FileBrowser';
import ChatPanel from './components/ChatPanel';
import FilePreview from './components/FilePreview';
import { api } from './services/api';

function App() {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileContent, setFileContent] = useState('');
  const [fileInfo, setFileInfo] = useState(null);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filesLoading, setFilesLoading] = useState(true);
  const [previewLoading, setPreviewLoading] = useState(false);
  const [status, setStatus] = useState(null);

  const loadFiles = useCallback(async () => {
    setFilesLoading(true);
    try {
      const data = await api.getFiles();
      if (data.files) {
        setFiles(data.files);
      }
    } catch (error) {
      console.error('Failed to load files:', error);
      addSystemMessage('Failed to load files. Make sure the backend is running.');
    } finally {
      setFilesLoading(false);
    }
  }, []);

  const loadStatus = useCallback(async () => {
    try {
      const data = await api.getStatus();
      setStatus(data);
    } catch (error) {
      console.error('Failed to load status:', error);
    }
  }, []);

  useEffect(() => {
    loadFiles();
    loadStatus();
  }, [loadFiles, loadStatus]);

  const addMessage = (content, type) => {
    setMessages((prev) => [...prev, { content, type }]);
  };

  const addSystemMessage = (content) => {
    addMessage(content, 'system');
  };

  const handleSelectFile = async (filePath) => {
    setSelectedFile(filePath);
    setPreviewLoading(true);
    try {
      const data = await api.readFile(filePath);
      if (data.error) {
        setFileContent('');
        setFileInfo(null);
        addSystemMessage(`Error reading file: ${data.error}`);
      } else {
        setFileContent(data.content);
        setFileInfo({
          path: data.path,
          size: data.size,
          extension: data.extension,
        });
      }
    } catch (error) {
      console.error('Failed to read file:', error);
      setFileContent('');
      setFileInfo(null);
      addSystemMessage('Failed to read file');
    } finally {
      setPreviewLoading(false);
    }
  };

  const handleSendChat = async (message) => {
    addMessage(message, 'user');
    setLoading(true);
    try {
      const data = await api.chat(message);
      if (data.error) {
        addSystemMessage(`Error: ${data.error}`);
      } else {
        addMessage(data.response, 'assistant');
      }
    } catch (error) {
      console.error('Failed to send chat:', error);
      addSystemMessage('Failed to send message. Check backend connection.');
    } finally {
      setLoading(false);
    }
  };

  const handleModifyFile = async (filePath, instruction) => {
    addMessage(`Modify ${filePath}: ${instruction}`, 'user');
    setLoading(true);
    try {
      const data = await api.modifyFile(filePath, instruction);
      if (data.error) {
        addSystemMessage(`Error: ${data.error}`);
      } else {
        addSystemMessage(
          `File modified successfully! Original: ${data.original_size} bytes → Modified: ${data.modified_size} bytes`
        );
        if (data.preview) {
          addMessage(data.preview, 'assistant');
        }
        // Refresh the file preview
        await handleSelectFile(filePath);
      }
    } catch (error) {
      console.error('Failed to modify file:', error);
      addSystemMessage('Failed to modify file. Check backend connection.');
    } finally {
      setLoading(false);
    }
  };

  const handleClearHistory = async () => {
    setLoading(true);
    try {
      await api.clearHistory();
      setMessages([]);
      addSystemMessage('Conversation history cleared');
    } catch (error) {
      console.error('Failed to clear history:', error);
      addSystemMessage('Failed to clear history');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>AI Agent - File Assistant</h1>
        {status && (
          <div className="status-info">
            <span className="model-badge">{status.model}</span>
            <span className="status-dot"></span>
            <span>Connected</span>
          </div>
        )}
      </header>

      <main className="app-main">
        <aside className="sidebar">
          <FileBrowser
            files={files}
            selectedFile={selectedFile}
            onSelectFile={handleSelectFile}
            loading={filesLoading}
          />
        </aside>

        <section className="chat-section">
          <ChatPanel
            messages={messages}
            selectedFile={selectedFile}
            onSendChat={handleSendChat}
            onModifyFile={handleModifyFile}
            onClearHistory={handleClearHistory}
            loading={loading}
          />
        </section>

        <aside className="preview-section">
          <FilePreview
            selectedFile={selectedFile}
            fileContent={fileContent}
            fileInfo={fileInfo}
            loading={previewLoading}
          />
        </aside>
      </main>
    </div>
  );
}

export default App;
